
public class prog_8 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
          int x=5;
          int y=7;
          System.out.println("Before swapping");
          System.out.println("x"+x+"y"+y );
          x=x+y;
          y=x-y;
          x=x-y;
          System.out.println("after swapping");
          System.out.println("x"+x+"y"+y);
          
	}

}
